function PositionControl(props) {
const position = props.position;
    if (position == 'left') {
        return (
            <div>
                <div className="col-6"><img src={`/static/` + props.imgName} /></div>
                <div className="discription col-6">
                    <h1>{props.prodTitle}</h1>
                    <div>{props.prodDisc}</div>
                </div>
            </div>
        );
    } else {
        return (
            <div>
                <div className="discription col-6">
                    <h1>{props.prodTitle}</h1>
                    <div>{props.prodDisc}</div>
                </div>
                <div className="col-6"><img src={`/static/` + props.imgName} /></div>
            </div>
        );        
    }
}

const Product = (props) => (
    <div className="product">
        <div className="container-fluid">
            <div className="content row">
                <PositionControl position={props.style} prodTitle={props.prodTitle}/>
            </div>
        </div>
    </div>
)

export default Product
