class PositionControl extends React.Component {
    constructor(props) {
        super(props)
    }
    render() {
    const position = props.position;
        if (position == 'left') {
            return (
                <div>
                    <div className="col-6"><img src={`/static/` + this.props.imgName} /></div>
                    <div className="discription col-6">
                        <h1>{this.props.prodTitle}</h1>
                        <div>{this.props.prodDisc}</div>
                    </div>
                </div>
            );
        } else {
            return (
                <div>
                    <div className="discription col-6">
                        <h1>{this.props.prodTitle}</h1>
                        <div>{this.props.prodDisc}</div>
                    </div>
                    <div className="col-6"><img src={`/static/` + this.props.imgName} /></div>
                </div>
            );        
        }
    }
}

const Product = (props) => (
    <div className="product">
        <div className="container-fluid">
            <div className="content row">
                <PositionControl position={props.style}/>
            </div>
        </div>
    </div>
)

export default Product
