const Product_1 = (props) => (
    <div className="product">
        <div className="container-fluid">
            <div className="content row">
                <div className="discription col-md-6">
                    <h1>{props.prodTitle}</h1>
                    <div>{props.prodDisc}</div>
                </div>
                <div className="col-md-6"><img src={`/static/` + props.imgName} /></div>
            </div>
        </div>
        <style jsx>{`
            .product {
                padding-top: 10%;
            }

            .product img {
                width: 20%;
            }

            .product .content {
                text-align: center;
            }

            .product .discription {
                margin-top: 3rem;
            }
        `}</style>
    </div>
)

export default Product_1
