function PositionControl(props) {
    if(props.position == 'left') {
         return (
            <div>
                <div className="col-6"><img src={`/static/` + props.imgName} /></div>
                <div className="discription col-6">
                    <h1>{props.prodTitle}</h1>
                    <div>{props.prodDisc}</div>
                </div>
            </div>
        );
    } else {
        return (
            <div>
                <div className="discription col-6">
                    <h1>{props.prodTitle}</h1>
                    <div>{props.prodDisc}</div>
                </div>
                <div className="col-6"><img src={`/static/` + props.imgName} /></div>
            </div>
        );
    }
}


const Product = (props) => (
    <div className="product">
        <div className="container-fluid">
            <div className="content row">
                <div className="col-md-6"><img src={`/static/` + props.imgName} /></div>
                <div className="discription col-md-6">
                    <h1>{props.prodTitle}</h1>
                    <div>{props.prodDisc}</div>
                </div>
            </div>
        </div>
        <style jsx>{`
            .product {
                padding-top: 10%;
            }

            .product img {
                width: 20%;
            }

            .product .content {
                text-align: center;
            }

            .product .discription {
                margin-top: 3rem;
            }
        `}</style>
    </div>
)

export default Product
