function PositionControl(props) {
    if (props.prodData.position == 'left') {
         return (
            <div className="container-fluid">
            <div className="content row">
                <div className="col-6"><img src={`/static/` + props.prodData.img} /></div>
                <div className="discription col-6">
                    <h1>{props.prodData.title}</h1>
                    <div>{props.prodData.disc}</div>
                </div>
            </div>
            </div>
        );
    } else {
        return (
            <div className="container-fluid">
            <div className="content row">
                <div className="discription col-6">
                    <h1>{props.prodData.title}</h1>
                    <div>{props.prodData.disc}</div>
                </div>
                <div className="col-6"><img src={`/static/` + props.prodData.img} /></div>
            </div>
            </div>
        );
    }
}

class Product extends React.Component {
    render() {
        const product = {
            img: this.props.imgName,
            title: this.props.prodTitle,
            disc: this.props.prodDisc,
            position: this.props.style
        }
        return(
            <div className="product">

                    <PositionControl prodData={product}/>
                        <style jsx global>{`
                            .product {
                                padding-top: 10%;
                            }

                            .product img {
                                width: 20%;
                            }

                            .product .content {
                                text-align: center;
                            }

                            .product .discription {
                                margin-top: 3rem;
                            }
                        `}</style>
                </div>
        );
    }
}

